var createPattern = function(path) {
  return {pattern: path, included: true, served: true, watched: false};
};

var initPhantomjs = function(files) {
  //files.unshift(createPattern(__dirname + '/adapter.js'));
  //files.unshift(createPattern(__dirname + '/boot.js'));
  //files.unshift(createPattern(__dirname + '/bind-polyfill.js'));
  files.unshift({pattern: __dirname + '/es5-shim.js', included: true, served: true, watched: false});
  files.unshift({pattern: __dirname + '/bind-polyfill.js', included: true, served: true, watched: false});
  console.log('POLYFIIIILL');
};

initPhantomjs.$inject = ['config.files'];

module.exports = {
  'framework:phantomjs': ['factory', initPhantomjs]
};